var windDirection= {};
var windspeedms=msg.payload.windspeed //wind speed in m/s
var windspeed = (windspeedms)*2.23694;//converted to mph
var windspeedmph=(msg.payload.windspeed.toFixed(1)); //display 1 decimal place
var temp=msg.payload.tempc
var location=msg.payload.location
var rise=msg.payload.sunrise
var sunrise = 3600000+Number(rise)*1000;//convert from seconds to milliseconds
var set=msg.payload.sunset
var sunset = 3600000+Number(set)*1000;//convert from seconds to milliseconds

/* function for converting meteorogical degree to text */

var degToCard = function(deg){
if (deg>11.25 && deg<=33.75){
return "Nord-Nord-Ost";
  }else if (deg>33.75 && deg<56.25){
return "Nord-Ost";
  }else if (deg>56.25 && deg<78.75){
return "Ost-Nord-Ost";
  }else if (deg>78.75 && deg<101.25){
return "Ost";
  }else if (deg>101.25 && deg<123.75){
return "Ost-Süd-Ost";
  }else if (deg>123.75 && deg<146.25){
return "Süd-Ost";
  }else if (deg>146.25 && deg<168.75){
return "Süd-Süd-Ost";
  }else if (deg>168.75 && deg<191.25){
return "Süd";
  }else if (deg>191.25 && deg<213.75){
return "Süd-Süd-West";
  }else if (deg>213.75 && deg<236.25){
return "Süd-West";
  }else if (deg>236.25 && deg<258.75){
return "West-Süd-West";
  }else if (deg>258.75 && deg<281.25){
return "West";
  }else if (deg>281.25 && deg<303.75){
return "West-Nord-West";
  }else if (deg>303.75 && deg<326.25){
return "Nord-West";
  }else if (deg>326.25 && deg<348.75){
return "Nord-Nord-West";
  }else{
return "Nord"; 
  }
}
/* End of function for converting meteorogical degree to text */

windDirection.payload = degToCard(msg.payload.winddirection);
var winddirection=windDirection.payload
msg.payload = "location: " + location +  "<br>" +
" Current Temperature " + temp +"c"+ "<br>" +
" Wind: " + windspeedmph +" mph" + "<br>" +
" Direction: " + winddirection +  "<br>" +
" Sunrise: " +  (new Date(sunrise)).toLocaleTimeString()+ "<br>" + 
" Sunset: " + (new Date(sunset)).toLocaleTimeString();
return msg;

